import React, { useState } from 'react'
import ExpenseItem from './ExpenseItem';
import './Expenses.css'
import Card from '../UI/Card'
import ExpensesFilter from './ExpensesFilter';

const Expenses = (props) => {
    const [filteredYear, setFilteredYear] = useState("2021");

    const filterChangeHandler = (enteredYear) => {
        setFilteredYear(enteredYear);
    }

    const filteredItems = props.items.filter((expense) => {
        return (expense.date.getFullYear().toString() === filteredYear);
    })


    let expenseContent = <p>No Expense Content</p>;

    if (filteredItems.length > 0) {
        expenseContent = filteredItems.map((expenses) =>
                <ExpenseItem
                    key={expenses.id}
                    title={expenses.title}
                    amount={expenses.amount}
                    date={expenses.date}>
                </ExpenseItem>)
    }

return (
    <div>
        <Card className="expenses">
            <ExpensesFilter selected={filteredYear} onFilterChange={filterChangeHandler}></ExpensesFilter>
            {expenseContent};
        </Card>
    </div>
);

}

export default Expenses;
