import React,{  useState} from 'react';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(true);

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.valus);
    console.log(event.target.value);
  }

  const formSubmissionHandler = (event) => {
      event.preventDefault();

      if(enteredName.trim() === '' )
      {
          setEnteredNameIsValid(false);
          return;
      }

      console.log(enteredName);
      setEnteredName('');
  }

  return (
    <form>
      <div className='form-control' onSubmit={formSubmissionHandler}>
        <label htmlFor='name'>Your Name</label>
        <input value={enteredName} type='text' id='name' onChange={nameInputChangeHandler}/>
        {!enteredNameIsValid && <p className="error-text">Name Should Not be Empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
